sys_script_include.script:
var JgUtils = Class.create();
JgUtils.prototype = {
	initialize: function() {
	},


	getUserRole : function(){
		var gr = new GlideRecord('x_195491_job_gra_0_jg_user_profile');
		gr.addQuery('user',gs.getUserID());
		gr.query();

		if(gr.getRowCount() != 1){
			return null;
		}else{
			gr.next();
			return gr.getValue('role');
		}
	},

	getStage : function(stage_key){
		var gr = new GlideRecord('x_195491_job_gra_0_stage_mapping');
		gr.addEncodedQuery("active=true^value=" + stage_key);
		gr.query();

		if(gr.next() && !gr.hasNext()){
			return gr.getValue('sys_id');
		}else{
			return null;
		}
	},

	type: 'JgUtils'
};
